import java.util.*;

public class Solution
{
    public static void main(String[] args) {

        Scanner sc = new Scanner(System.in);

        //String s = "aec";
        //String t = "ahbgdc";

        String s = "leeeeetcode";
        String t = "";

        boolean result = isSubsequence(s, t);
        System.out.println(result);
    }

    // is s is subsequence of t
    static boolean isSubsequence(String s, String t)
    {
        if (s == null && t == null) {
            return true;
        }

        if (s.length() == 0) {
            return true;
        }

        if (s.length() > t.length()) {
            return false;
        }

        char[] sc = s.toCharArray();
        char[] tc = t.toCharArray();

        return isSubsequence(sc, tc, 0, 0);
    }

    static boolean isSubsequence(char[] sc, char[] tc, int i, int j)
    {
        if (i == sc.length && j >= tc.length) {
            return true;
        } else if (j >= tc.length) {
            return false;
        }

        if (i < sc.length && sc[i] == tc[j]) {
            return isSubsequence(sc, tc, i+1, j+1);
        } else {
            return isSubsequence(sc, tc, i, j+1);
        }
    }
}
